<?phpif (!defined('BASEPATH'))    exit('No direct script access allowed');/** * @property CI_Loader $load * @property CI_Form_validation $form_validation * @property CI_Input $input * @property CI_Email $email * @property CI_DB_active_record $db * @property CI_DB_forge $dbforge * @property CI_Table $table * @property CI_Session $session * @property CI_FTP $ftp * */class MY_Controller extends CI_Controller {    public $o_response;    public $menu_focus;    public $upload_path;    public $user_id;    function __construct() {        parent::__construct();                $this->user_id = $this->session->userdata('user_email');                $this->upload_path = $_SERVER['DOCUMENT_ROOT'].'/namviet-gl/namviet/uploads/';//                $this->o_response = new stdClass();         $this->o_response->response = 'success';        $this->o_response->content = '';                $is_login = $this->session->userdata('is_login');        if (!isset($is_login) || $is_login != true) {                if ($this->input->is_ajax_request()) {                        $this->o_response->response = 'not_login';                        echo json_encode($this->o_response);                        exit;                }                else {//                        $return_url = $_SERVER['SERVER_NAME'].$_SERVER['PHP_SELF'];                        $return_url = $this->uri->uri_string();//                        var_dump($return_url);exit;//                        redirect(base_url() . 'user/login/?url='.$return_url);//                        $return_url = urlencode('message/list_message/inbox');                        redirect(base_url().'user/login/'.$return_url);                }        }        //        function my_redirect($link){//                echo "<script type='text/javascript'>document.location = '$link';</script>";//        }    }    }?>